mapper.update:
UPDATE maoche_push_task
SET status = #{status}

	id in

		#{id}

UPDATE maoche_push_task
SET status = #{newStatus}

	id = #{id}
	and
	status = #{oldStatus}

UPDATE maoche_push_task
SET
	status = #{status},
	publish_date = #{publishDate}

	id in

		#{id}

mapper.select:
select * from maoche_push_task

	status = #{status}
	and
	publish_date  <=  #{publishDate}
	order by create_date asc
	limit 10

mapper.update:
UPDATE maoche_push_task
SET status = "FINISHED",
	finished_date = #{finishedDate}

	id = #{id}
	and
	status = "PUSHING"
