mapper.update:
UPDATE maoche_push_task
SET status = #{status}

	id in

		#{id}

UPDATE maoche_push_task
SET status = #{newStatus}

	id = #{id}
	and
	status = #{oldStatus}

UPDATE maoche_push_task
SET
	status = #{status},
	publish_date = #{publishDate}

	id in

		#{id}

mapper.select:
select * from maoche_push_task

	status = #{status}
	and
	publish_date  <=  #{publishDate}
	order by create_date asc
	limit #{limit}

mapper.update:
UPDATE maoche_push_task
SET status = "FINISHED",
	finished_date = #{finishedDate}

	id = #{id}
	and
	status = "PUSHING"

UPDATE maoche_push_task


		title = #{title},


		push_type = #{pushType},


		detail = #{detail},


		content = #{content}



	id = #{id}

mapper.select:
select resource_id as resourceId, resource_type as resourceType, count(resource_id) as cnt from maoche_push_task where status = #{status} and resource_type = #{resourceType} and resource_id in
#{resourceId}
group by resource_id
